    margin-bottom: 1.5rem;
  }
  .section--contact__content .contact__mail__message {
    padding: 0 2rem;
    margin-bottom: 1.5rem;
  }
  .section--contact__content .contact__mail__submit {
    padding: 1.5rem 0;
  }

  .section--contact__content .contact__mail__submit:hover {
    background: var(--main-dark-col);
    color: var(--main-bg-col);
  }

  .modal {
    width: 35rem;
    height: 30rem;

    top: 30vh;
    left: calc((100vw - 35rem) / 2);

    padding: 0 2rem;
  }

  .modal span {
    font-size: 1.6rem;
  }

  .modal--spinner {
    position: absolute;
    transition: all 0.5s;
  }

  .modal .modal__close-btn {
    font-size: 1.6rem;
  }
}

sources.components.css:
@import url("https://fonts.googleapis.com/css2?family=Noto+Sans+KR&family=Raleway&display=swap");
@import url(./variables.css);

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  font-size: 10px;
}

body {
  height: 100vh;
  margin: 0;
  font-family: "Raleway", sans-serif;
  scroll-behavior: smooth;

  background-image: linear-gradient(-20deg, #b721ff 0%, #21d4fd 100%);
  background-attachment: fixed;

  color: #fff;
}

.section {
  height: 100vh;
  width: 100vw;
  overflow-y: hidden !important;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

.section__title1 {
  font-weight: 100;
  font-size: 3rem;
}
.section__title2 {
  font-weight: 600;
  font-size: 3.5rem;
}

.header {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 2;
  width: 100vw;
  padding: 1rem 2rem;
  transition: all 0.2s ease-in-out;
  backdrop-filter: blur(0.5rem);
}

.header--opaque {
  background: rgba(255, 255, 255, 0.3);
  transition: all 0.2s ease-in-out;
  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
  z-index: 2;
}

.header--opaque .navbar a {
  color: #fff;
}

.header .navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.header--transparent .navbar a {
  color: var(--main-bg-col);
}

.header .navbar .navbar__brand {
  font-size: 3rem;
  font-weight: 700;
}

.header .navbar .navbar__menu {
  display: flex;
  justify-content: flex-end;

  width: 50vw;
}

.header .navbar .navbar__menu .navbar__menu__item {
  margin-left: 1rem;
}

.header .navbar .navbar__menu .navbar__menu__item a {
  font-size: 1.6rem;
  padding: 0.5rem;
}
.header .navbar .navbar__menu .navbar__menu__item a:hover {
  color: var(--main-bg-col);
}

.header--mobile {
  display: none;
}

.header .navbar .navbar__menu--mobile {
  display: none;
}

.item--active {
  background: var(--main-red-col) !important;
}

.container {
  position: relative;
  scroll-snap-type: y mandatory;
  overflow: auto;
}

.container .section {
  position: relative;
  scroll-snap-align: start;
}

.section--home {
  display: flex;
  align-items: center;
  padding: 10rem;
}

.section--home .section--home__content {
  display: flex;
  flex-direction: column;
}

.section--home .section--home__content .section--home__content__text {
  width: 55rem;

  font-size: 7rem;
  color: var(--main-bg-col);
  border-radius: 1rem;
  margin-bottom: 1rem;
  opacity: 0;
  background: rgba(255, 255, 255, 0.3);
  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
  border: 1px solid var(--main-bg-col);
  padding: 1rem;
}
.section--home .section--home__content .section--home__content__text:hover {
  transition: all 0.5s;
  transform: translateY(1rem);
}
.section--home .section--home__content .home__title1 {
  transition: transform 1.5s ease-in-out;
}
.section--home .section--home__content .home__title1:hover {
  transform: translateY(-2rem);
}
.section--home .section--home__content .home__title2 {
  transform: translateY(150px);
  transition: transform 2s ease-in-out;
}

.section--home .section--home__content .home__title2:hover {
  transform: translateY(2rem);
}

.section--home .section--home__content .lazy-up {
  opacity: 1;
  transform: translateY(0px);
}

.section--about {
  display: flex;
  align-items: center;
  justify-content: center;
}

.section--about__content {
  width: 80vw;
  display: grid;
  grid-template-rows: 70% 30%;
  grid-template-columns: repeat(2, 50%);
  grid-template-areas: "text picture" "info picture";
}

.section--about__content .section--about__text {
  grid-area: text;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  color: var(--main-black-col);

  opacity: 0;
  transition: all 1.5s ease-in-out;
  transform: translateY(100px);
}

.section--about__content .section--about__text h1 {
  transition: opacity 1s;
  color: var(--main-bg-col);
}

.section--about .section--about__content .lazy-up {
  opacity: 1;
  transform: translateY(0px);
}

.section--about__text .about__title1 {
  margin-bottom: 0.5rem;
}

.section--about__text .about__title2 {
  margin-bottom: 2rem;
}

.section--about__text h1,
p {
  width: 70%;
}

.section--about__text p {
  font-size: 2rem;
  font-family: "Noto Sans KR", sans-serif !important;
  color: var(--main-bg-col);
}

.section--about__content .section--about__picture {
  grid-area: picture;
}

.section--about__picture img {
  max-height: 500px;
  border-radius: 0.25rem;

  opacity: 0;
  transition: all 1.5s ease-in-out;
  transform: translateY(130px);
}

.section--about__content .section--about__info {
  grid-area: info;
  display: flex;
  flex-direction: column;
  align-items: center;

  opacity: 0;
  transform: translateY(150px);
  transition: all 1s ease-in-out;
}

.section--about__content .section--about__info .section--about__info__item {
  background: rgba(255, 255, 255, 0.3);
  border: 1px solid var(--main-bg-col);
  border-radius: 0.5rem;
  display: flex;

  padding: 1rem 2rem;
  width: 40rem;

  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
}
.section--about__content
  .section--about__info
  .section--about__info__item:first-of-type {
  margin-bottom: 2rem;
}

.section--about__content .section--about__info .section--about__info__item i {
  color: var(--main-bg-col);
  font-size: 2.5rem;
  width: 4rem;
}

.section--about__content .section--about__info .section--about__info__item h2 {
  font-size: 1.6rem;
  font-weight: 400;
}

.section--skills {
  display: flex;
  justify-content: center;
  align-items: center;
}

.section--skills__content {
  width: 100%;
  height: 100%;

  padding: 10vh 20rem 5vh 20rem;
}

.section--skills .section--skills__content .lazy-up {
  opacity: 1;
  transform: translateY(0px);
  transition: all 1s ease-in-out;
}

.section--skills__content .section--skills__title {
  height: 20%;
  text-align: center;

  opacity: 0;
  transform: translateY(100px);
}

.section--skills__title .skills__title1 {
  font-weight: 100;
}

.section--skills__title .skills__title2 {
  margin-bottom: 2rem;
}

.section--skills__content .skills__slider {
  width: 100%;
  max-width: 120rem;
  height: 80%;

  opacity: 0;
  transform: translateY(120px);
}

.section--skills__content .skills__slider .skills__slide {
  height: calc((80%) / 2);
  display: flex;
  justify-content: center;
  align-items: center;
}

.section--skills__content .skills__slider .skills__slide__icon {
  height: 180px;
  width: 120px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  background: rgba(255, 255, 255, 0.3);

  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
}

.section--skills__content .skills__slider .skills__slide__icon i {
  font-size: 7rem;
  color: var(--main-bg-col);
}
.section--skills__content .skills__slider .skills__slide__icon h1 {
  color: var(--main-bg-col);
  font-size: 1.6rem;
  font-weight: 400;
}

.slider__pagination .swiper-pagination-bullet {
  width: 1rem;
  height: 1rem;
  text-align: center;
  opacity: 1;
  background: var(--main-dark-col);
}

.slider__pagination .swiper-pagination-bullet-active {
  color: #fff;
  background: var(--main-bg-col);
}

.section--works {
  display: flex;
  justify-content: center;
}
.section--works .section--works__content {
  height: 100%;
  width: 100%;
}

.section--works .section--works__content .lazy-up {
  opacity: 1;
  transform: translateY(0px);
  transition: all 1s ease-in-out;
}

.section--works__content .section--works__title {
  text-align: center;

  opacity: 0;
  transform: translateY(100px);

  margin-bottom: 5vh;
}

.section--works__title .works__title1 {
  font-weight: 100;
}

.section--works__title .works__title2 {
  margin-bottom: 2rem;
}

.section--works__content .works__slider {
  width: 90vw;
  height: 80vh;

  display: flex;
  justify-content: center;

  opacity: 0;
  transform: translateY(100px);
}
.section--works__content .works__slider .works__slider__wrapper {
  height: 90%;
  width: 100%;
}

.section--works__content .works__slider .works__slide {
  background: rgba(255, 255, 255, 0.3);
  border: 1px solid var(--main-bg-col);
  display: flex;
  justify-content: center;
  align-items: top;
  border-radius: 1rem;
  transition: all 0.5s;
  padding: 1rem 0;
  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
}

.section--works__content .works__slider .works__slide .works__slide__content {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
}
.section--works__content
  .works__slider
  .works__slide
  .works__slide__content
  .works__slide__title {
  color: var(--main-bg-col);
  font-size: 2.5rem;
  margin-bottom: 1rem;
  transition: all 0.5s;
}

.section--works__content
  .works__slider
  .works__slide
  .works__slide__content
  .works__slide__picture-wrapper {
  width: 85%;
  border-radius: 0.5rem;
  overflow: hidden;
  position: relative;
}

.section--works__content
  .works__slider
  .works__slide
  .works__slide__content
  .works__slide__picture-wrapper
  .works__slide__picture {
  transition: all 0.5s ease-in-out;
  width: 100%;
  z-index: 1;
}

.section--works__content
  .works__slider
  .works__slide
  .works__slide__content
  .works__slide__picture:hover {
  transform: scale(1.1);
}

.section--works__content
  .works__slider
  .works__slide
  .works__slide__content
  .works__slide__btns-wrapper {
  display: flex;
  position: absolute;
  bottom: 1rem;
  left: calc((100% - 10rem) / 2);
  justify-content: space-between;
  z-index: 2;
  border: 1px solid var(--main-bg-col);
}

.works__slide__btns-wrapper .works__slide__btn {
  background: rgba(255, 255, 255, 0.3);

  border: none;
  transition: all 0.2s ease-in-out;
  width: 5rem;
  height: 5rem;

  display: flex;
  align-items: center;
  justify-content: center;
}

.works__slide__btns-wrapper .works__slide__btn:hover > a > i {
  transform: scale(1.3);
}

.works__slide__btns-wrapper .works__slide__btn a {
  display: flex;
  align-items: center;
  justify-content: center;

  height: 100%;
  width: 100%;
}

.works__slide__btns-wrapper .works__slide__btn a i {
  transition: all 0.3s;
  font-size: 2rem;
  color: var(--main-dark-col);
}

.works__slide__content .works__slide__details {
  color: var(--main-bg-col);
  padding: 1rem 0;
  transition: all 0.5s;
  font-family: "Noto Sans KR", sans-serif !important;
}
.works__slide__content .works__slide__details h1 {
  font-size: 2rem;
}

.works__slide__content .works__slide__details .works__slide__function {
  padding: 1rem;
}
.works__slide__content
  .works__slide__details
  .works__slide__function
  .works__slide__function__item {
  list-style-type: disc;
  font-size: 1.6rem;
  padding: 0.1rem 0;
}

.section--contact {
  display: flex;
  justify-content: center;
  align-items: center;
}

.section--contact .section--contact__content {
  width: 80%;
  height: 90%;
  display: grid;
  grid-template-rows: 25% 70%;
  row-gap: 5%;
  column-gap: 5%;
  grid-template-columns: repeat(2, 47.5%);
  grid-template-areas:
    "title title"
    "contact mail";
}

.section--contact .section--contact__content .lazy-up {
  opacity: 1 !important;
  transform: translateY(0px) !important;
  transition: all 1.5s ease-in-out;
}
.section--contact .section--contact__content .section--contact__title {
  grid-area: title;
  margin: 0 auto;
  text-align: center;
  align-self: flex-end;

  opacity: 0;
  transform: translateY(80px);
}

.section--contact__content .section--contact__info {
  grid-area: contact;
  display: flex;
  flex-direction: column;
  align-items: flex-end;

  opacity: 0;
  transform: translateY(100px);
}

.section--contact__content .section--contact__info__item {
  background: rgba(255, 255, 255, 0.3);
  border: 1px solid var(--main-bg-col);
  border-radius: 1rem;
  display: flex;
  padding: 3rem 3rem;
  align-items: center;
  width: 40rem;
  margin-bottom: 3.5rem;
  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
}
.section--contact__content .section--contact__info__item i {
  font-size: 2rem;
}
.section--contact__content .section--contact__info__item h2 {
  font-size: 1.6rem;
  margin-left: 2rem;
  font-weight: 400;
}
.section--contact__content .section--contact__info__item h2 a {
  color: #fff;
  font-size: 1.6rem;
}

.section--contact__content .contact__mail {
  grid-area: mail;

  display: flex;
  flex-direction: column;

  opacity: 0;
  transform: translateY(120px);
}
.section--contact__content .contact__mail input {
  border-radius: 1rem;
  border: 1px solid #fff;
  width: 45rem;
  background: rgba(255, 255, 255, 0.5);
  color: #fff;
  box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
}

.section--contact__content .contact__mail__name {
  height: 5rem;
  margin-bottom: 2rem;
  font-size: 2rem;
  padding-left: 2rem;
}
.section--contact__content .contact__mail__address {
  height: 5rem;
  margin-bottom: 2rem;
  font-size: 2rem;
  padding-left: 2rem;
}
.section--contact__content .contact__mail__message {
  height: 20rem;
  margin-bottom: 2rem;
  font-size: 2rem;
  padding-left: 2rem;
}
.section--contact__content .contact__mail__submit {
  padding: 2rem 0;
  font-size: 2rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.35s ease-in-out;
  color: #fff;
}

.section--contact__content .contact__mail__submit:hover {
  background: var(--main-bg-col);
  color: var(--main-dark-col);
}

.modal {
  width: 50rem;
  height: 30rem;
  z-index: 10;
  position: fixed;
  top: 20vh;
  left: calc((90vw - 30rem) / 2);

  transition: 0.3s all;

  background: rgba(255, 255, 255, 0.5);
  backdrop-filter: blur(5px);
  border: 1px solid var(--main-bg-col);
  border-radius: 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.modal span {
  font-size: 2rem;
  color: var(--main-black-col);
  font-family: "Noto Sans KR", sans-serif !important;
  transition: all 0.5s;
  opacity: 0;
  padding: 2rem;
  text-align: center;
}

.modal--spinner {
  position: absolute;
  transition: all 0.5s;
}

.modal .modal__close-btn {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
  border: 1px solid var(--main-bg-col);

  width: 5rem;
  height: 5rem;
  border-radius: 1rem;
  font-size: 2rem;
  cursor: pointer;
  transition: all 0.5s;
  opacity: 0;
}

.modal .modal__close-btn:hover {
  background: var(--main-bg-col);
  color: var(--main-dark-col);
}

.modal--hidden {
  display: none;
  opacity: 0;
  transition: 0.3s all;
}

@media screen and (max-width: 768px) {
  .header {
    display: flex;
    flex-direction: column;
    padding: 0;
  }

  .section {
    overflow-x: hidden;
  }

  .header .header--mobile {
    display: flex;
    margin: 0 auto;
    width: 100%;
    padding: 0 2rem;
    height: 10vh;
    justify-content: space-between;
    align-items: center;
    z-index: 2;

    transition: all 0.3s;
  }

  .header .header--mobile {
    background: var(--main-bg-col);
  }

  .header .header--mobile .navbar__brand--mobile {
    display: block;
    font-size: 2rem;
    font-weight: 700;
    color: var(--main-dark-col);
  }

  .header .navbar .navbar__menu {
    display: none;
  }
  .header .navbar .navbar__brand {
    display: none;
  }

  .header--mobile .navbar__hamburger {
    grid-area: hamburger;
    display: block;
    background: transparent;
    border: none;
    margin-right: 3rem;
  }

  .header--mobile .navbar__hamburger i {
    color: var(--main-dark-col);
    font-size: 2rem;
  }

  .header--mobile .navbar__hamburger:hover {
    cursor: pointer;
  }

  .header .navbar .mobile--white {
    color: var(--main-black-col);
  }

  .header .navbar .navbar__menu--mobile {
    position: absolute;
    transform: translateY(-500px);
    margin: 0;
    grid-area: menu;
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 40vh;
    width: 100vw;
    transition: transform 1s, background 0.2s, color 0.2s ease-in-out;
    background: var(--main-bg-col);
    box-shadow: 0 0.5rem 1rem rgba(100, 100, 100, 0.3);
  }

  .header .navbar .mobile--active {
    transition: transform 1s, background 0.2s, color 0.2s ease-in-out;
    transform: translateY(0px);
    top: 10vh;
  }

  .header .navbar .mobile--active .navbar__menu__item a {
    color: var(--main-black-col);
  }

  .header .navbar .navbar__menu--mobile .navbar__menu__item {
    text-align: center;
    height: 4rem;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .header .navbar .navbar__menu--mobile .navbar__menu__item a {
    font-size: 2rem;
    color: var(--main-dark-col);
    font-weight: 600;
  }

  .section {
    min-height: 100vh;
  }

  .section--home {
    display: flex;
    align-items: center;
    padding: 0;
  }

  .section--home .section--home__content {
    justify-content: center;
    align-items: center;
    padding: 0 5rem;
  }

  .section--home .section--home__content .section--home__content__text {
    font-size: 3.2rem;
    width: 30rem;
  }

  .section--about {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .section--about__content {
    height: 90vh;
    display: grid;
    grid-template-columns: 100%;
    grid-template-rows: 30% 20% 50%;

    grid-template-areas:
      "text"
      "info"
      "picture";
  }

  .section--about__content .section--about__text {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
  }

  .section--about__text .about__title1 {
    font-weight: 100;
    font-size: 3rem;
    margin-bottom: 0.5rem;
  }

  .section--about__text .about__title2 {
    font-weight: 600;
    font-size: 3.2rem;
    margin-bottom: 1rem;
  }

  .section--about__text h1,
  p {
    width: 80%;
  }

  .section--about__text p {
    font-size: 1.6rem;
  }

  .section--about__content .section--about__picture {
    display: flex;
    align-items: center;
    justify-content: flex-end;
  }

  .section--about__picture img {
    max-height: 250px;
    border-radius: 0.5rem;
  }

  .section--about__content .section--about__info {
    transform: translateY(100px);
    transition: all 1.5s ease-in-out;
  }

  .section--about__content .section--about__info .section--about__info__item {
    padding: 1rem 2rem;
    width: 100%;
  }
  .section--about__content
    .section--about__info
    .section--about__info__item:first-of-type {
    margin-bottom: 0.2rem;
  }

  .section--skills__content {
    padding: 10vh 0;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
  }
  .section--skills__content .skills__slider {
    height: 60vh;
  }

  .section--skills__content .skills__slider .skills__slide__icon {
    height: 150px;
    width: 100px;
  }

  .section--skills__content .skills__slider .skills__slide__icon i {
    font-size: 5.5rem;
    margin-bottom: 0.5rem;
  }
  .section--skills__content .skills__slider .skills__slide__icon h1 {
    font-size: 1.6rem;
  }

  .swiper-pagination-bullet {
    width: 0.7rem;
    height: 0.7rem;
  }

  .works__slide__content .works__slide__details {
    padding: 2rem 3rem;
  }

  .works__slide__content
    .works__slide__details
    .works__slide__function
    .works__slide__function__item {
    padding: 0.5rem 0;
  }

  .section--contact {
    height: 120vh;
  }

  .section--contact .section--contact__content {
    padding: 5vh 0;
    width: 100%;
    height: 100%;
    display: grid;
    grid-template-rows: 10% 25% 55%;
    grid-template-columns: 100%;
    grid-template-areas:
      "title"
      "contact"
      "mail";
  }

  .section--contact .section--contact__content .section--contact__title {
    opacity: 0;
    transform: translateY(80px);
  }

  .section--contact__content .section--contact__info {
    align-items: center;
  }

  .section--contact__content .section--contact__info__item {
    padding: 1.5rem 1rem;
    margin-bottom: 1.5rem;
    width: 90%;
  }

  .section--contact__content .contact__mail {
    align-items: center;
  }
  .section--contact__content .contact__mail input {
    border-radius: 1rem;
    border: 1px solid #fff;
    width: 90%;
  }

  .section--contact__content .contact__mail__name {
    padding: 0 2rem;
    margin-bottom: 1.5rem;
  }
  .section--contact__content .contact__mail__address {
    padding: 0 2rem;
    margin-bottom: 1.5rem;
  }
  .section--contact__content .contact__mail__message {
    padding: 0 2rem;
    margin-bottom: 1.5rem;
  }
  .section--contact__content .contact__mail__submit {
    padding: 1.5rem 0;
  }

  .section--contact__content .contact__mail__submit:hover {
    background: var(--main-dark-col);
    color: var(--main-bg-col);
  }

  .modal {
    width: 35rem;
    height: 30rem;

    top: 30vh;
    left: calc((100vw - 35rem) / 2);

    padding: 0 2rem;
  }

  .modal span {
    font-size: 1.6rem;
  }

  .modal--spinner {
    position: absolute;
    transition: all 0.5s;
  }

  .modal .modal__close-btn {
    font-size: 1.6rem;
  }
}
